import React, { useState } from "react";
import ReactDOM from "react-dom";

//Components
const Header = ({ title }) => {
	return <h1> {title} </h1>;
};

const Part = (props) => {
	return (
		<p>
			{props.excerciseName} {props.exerciseNum}
		</p>
	);
};

const Total = (props) => {
	return <p>Number of exercises {props.total}</p>;
};

const ItemCount = ({ name, count }) => {
	return (
		<p>
			{name} {count}
		</p>
