import React, { useState } from "react";
import ReactDOM from "react-dom";

//Components
const Header = ({ title }) => {
	return <h1> {title} </h1>;
};

const Total = ({ message, total }) => {
	return (
		<p>
			{" "}
			{message} {total}
		</p>
	);
};

const ItemCount = ({ name, count }) => {
	return (
		<p>
			{name} {count}
		</p>
	);
};

const Average = ({ good, neutral, bad }) => {
	return <p>average {(good - bad) / (good + neutral + bad)}</p>;
};

const Positive = ({ good, neutral, bad }) => {
	return <p>positive {(good * 100.0) / (good + neutral + bad)} %</p>;
};

const Statistic = ({ text, value }) => {
	return (
		<p>
			{text} {value}
		</p>
	);
};

const Statistics = ({ good, neutral, bad, text }) => {
	if (good + neutral + bad === 0) {
		return <p>No feedback given</p>;
	}

	return (
		<div>
			<ItemCount name="good" count={good} />
			<ItemCount name="neutral" count={neutral} />
			<ItemCount name="bad" count={bad} />
			<Total message="all" total={good + neutral + bad} />
			<Average good={good} neutral={neutral} bad={bad} />
			<Positive good={good} neutral={neutral} bad={bad} />
		</div>
	);
};

const Button = ({ text, handler }) => {
	return (
		<div>
			<button onClick={handler(text)}>{text}</button>
		</div>
	);
};

const App = () => {
	// save clicks of each button to its own state
	const [good, setGood] = useState(0);
	const [neutral, setNeutral] = useState(0);
	const [bad, setBad] = useState(0);

	//event handlers for feedback button
	function handleFeedbackButton(feedbackType) {
		if (feedbackType === "good") {
			return () => {
				const counter = good + 1;
				setGood(counter);
			};
		}

		if (feedbackType === "neutral") {
			return () => {
				const counter = neutral + 1;
				setNeutral(counter);
			};
		}

		if (feedbackType === "bad") {
			return () => {
				const counter = bad + 1;
				setBad(counter);
			};
		}
	}

	return (
		<div>
			<Header title={"Give feedback"} />
			<Button text="good" handler={handleFeedbackButton} />
			<Button text="neutral" handler={handleFeedbackButton} />
			<Button text="bad" handler={handleFeedbackButton} />
			<Header title={"Statistics"} />
			<Statistics good={good} neutral={neutral} bad={bad} />
		</div>
	);
};

ReactDOM.render(<App />, document.getElementById("root"));

